import React from 'react'
import axios from "axios";

function Todo(props) {
    const deleteItem = (e) =>{
        e.preventDefault();
        console.log(props.id)
        axios
        .delete(`http://127.0.0.1:8000/api/todos/${props.id}/`)
        .then((res) => props.refreshTodo);
    };





    return (
        <div>
            <li className="list-group-item d-flex justify-content-between align-items-center">
                {props.title}
                <span className="badge badge-primary badge-pill" onClick={(e)=>deleteItem(e)}>delete</span>
            </li>
        </div>
    )
}

export default Todo
