import './App.css';
import React, { useState, useEffect } from 'react';
import axios from "axios";
import Todo from "./components/Todo.js"


function App() {
  const [todos, SetTodos] = useState([])
  const [items, setItems] = useState({ title: "test", description: "etst", completed: false })


  useEffect(() => {

    axios
      .get("http://127.0.0.1:8000/api/todos/")
      .then((res) => {
        console.log(res.data)
        SetTodos(res.data)
      })
      .catch((err) => console.log(err))


  }, [])

  const refreshTodo = () => {
    axios
      .get("http://127.0.0.1:8000/api/todos/")
      .then((res) => {
        console.log(res.data)
        SetTodos(res.data)
      })
      .catch((err) => console.log(err))
  }

  const addItem = (e) => {

    e.preventDefault();
    axios.post("http://127.0.0.1:8000/api/todos/", items)
      .then((res) => {
        console.log(res.data)
        refreshTodo()
      })
      .catch((err) => console.log(err))
  }

  const deleteItem = (e) => {
    console.log(e)
    axios.delete("http://127.0.0.1:8000/api/todos/${e}/", items)
      .then((res) => {
        console.log(res.data)
        refreshTodo()
      })
      .catch((err) => console.log(err))
  } 


  return (

    <div className="App">
      {items.title}
      <ul class="list-group">
        {todos.map((todo, index) => (
          <Todo
            title={todo.title}
            description={todo.description}
            delete = {deleteItem}


          />
        ))}
      </ul >
      <div class="input-group mb-3">
      <input type="text" class="form-control" placeholder="Add Todo" value ={items.title} onChange={ e => setItems({ title: e.target.value, description: "etst", completed: false })}/>
        <div class ="input-group-append">
        <button class ="btn btn-outline-secondary" type ="button"  onClick={(e) => addItem(e)}> Add Item</button>
        </div>
      </div>



    </div>

  );
}

export default App;
