import './App.css';
import React, { useState,useEffect } from 'react';
import axios from "axios";
import todo from "./components/Todo.js"


function App() {
  const [todos,SetTodos] = useState([])
  const [items,setItems] = useState({title:"test", description: "etst", completed: false })

  useEffect(() => {

    axios
    .get("http://127.0.0.1:8000/api/todos/")
    .then((res) => {
      console.log(res.data)
      SetTodos(res.data)
    })
    .catch((err) => console.log(err))


  }, [])

  const refreshTodo = () => {
    axios
    .get("http://127.0.0.1:8000/api/todos/")
    .then((res) => {
      console.log(res.data)
      SetTodos(res.data)
    })
    .catch((err) => console.log(err))
  }

  const addItem = (e) => {
    e.preventDefault();
    axios.post("http://127.0.0.1:8000/api/todos/",items)
    .then((res) => {
      console.log(res.data)
      refreshTodo()
    })
    .catch((err) => console.log(err))
  } 


  return (
    <div className="App">
       {todos.map((todo, index) => (
          <Todo
            key={index}
            index={index}
            todo={todo}
          />
        ))} 
         <button onClick={(e) => addItem(e)}> Add Item</button>

    </div>
  );
}

export default App;
